    // const url = category ? `${ENDPOINTS.WELLNESS.LEARNING_MATERIALS}?category=${category}` : ENDPOINTS.WELLNESS.LEARNING_MATERIALS;
    // return httpClient.get<LearningMaterial[]>(url);

    // DUMMY DATA - Comment out when connecting to backend
    await new Promise(resolve => setTimeout(resolve, 700));
    if (category) {
      return DUMMY_LEARNING_MATERIALS.filter(mat => mat.category === category);
    }
    return DUMMY_LEARNING_MATERIALS;
  },

  getFeaturedMaterials: async (): Promise<LearningMaterial[]> => {
    // REAL API - Uncomment when backend is ready
    // return httpClient.get<LearningMaterial[]>(ENDPOINTS.WELLNESS.FEATURED_MATERIALS);

    // DUMMY DATA - Comment out when connecting to backend
    await new Promise(resolve => setTimeout(resolve, 500));
    return DUMMY_LEARNING_MATERIALS.filter(mat => mat.isFeatured);
  },

  getMaterialById: async (id: string): Promise<LearningMaterial | null> => {
    // REAL API - Uncomment when backend is ready
    // return httpClient.get<LearningMaterial>(ENDPOINTS.WELLNESS.LEARNING_MATERIAL_DETAIL(id));

    // DUMMY DATA - Comment out when connecting to backend
    await new Promise(resolve => setTimeout(resolve, 400));
    return DUMMY_LEARNING_MATERIALS.find(mat => mat.id === id) || null;
  },

  getRecommendations: async (riskLevel?: string): Promise<WellnessRecommendation[]> => {
    // REAL API - Uncomment when backend is ready
    // const url = riskLevel ? `${ENDPOINTS.WELLNESS.RECOMMENDATIONS}?riskLevel=${riskLevel}` : ENDPOINTS.WELLNESS.RECOMMENDATIONS;
    // return httpClient.get<WellnessRecommendation[]>(url);

    // DUMMY DATA - Comment out when connecting to backend
    await new Promise(resolve => setTimeout(resolve, 500));
    return DUMMY_RECOMMENDATIONS;
  },

  sendChatMessage: async (message: string): Promise<ChatMessage> => {
    // REAL API - Uncomment when backend is ready
    // return httpClient.post<ChatMessage>(ENDPOINTS.WELLNESS.CHAT, { message });

    // DUMMY DATA - Comment out when connecting to backend
    await new Promise(resolve => setTimeout(resolve, 1500));
    const responses: Record<string, string> = {
      default: "I'm here to help you with your cardiovascular health questions.",
      heart: "Your heart health is important! Regular exercise, a balanced diet, and stress management are key factors.",
      diet: "For heart health, focus on the Mediterranean diet: plenty of fruits, vegetables, whole grains, and fish.",
      exercise: "The American Heart Association recommends at least 150 minutes of moderate activity per week.",
      sleep: "Quality sleep is crucial for heart health. Aim for 7-9 hours nightly.",
      stress: "Chronic stress can harm your heart. Try deep breathing exercises, meditation, or yoga.",
      jendo: "The Jendo device measures your cardiovascular health through simple tests.",
    };
    const lowercaseMsg = message.toLowerCase();
    let response = responses.default;
    if (lowercaseMsg.includes('heart')) response = responses.heart;
    else if (lowercaseMsg.includes('diet') || lowercaseMsg.includes('food')) response = responses.diet;
    else if (lowercaseMsg.includes('exercise') || lowercaseMsg.includes('workout')) response = responses.exercise;
    else if (lowercaseMsg.includes('sleep')) response = responses.sleep;
    else if (lowercaseMsg.includes('stress')) response = responses.stress;
    else if (lowercaseMsg.includes('jendo') || lowercaseMsg.includes('device')) response = responses.jendo;
    return { id: `msg-${Date.now()}`, role: 'assistant', content: response, timestamp: new Date().toISOString() };
  },
};

